<?	class UCShowClassAddForm extends UseCase 	{		function action($p) 		{			global $db;			if(isset($_GET['id']) &&  ereg("([a-z]+)", $_GET['id'])) {			 	$id = $_GET['id'];								$cf = new ClassFactory;        	                $s .= $cf->showAddForm($this, $id);						}			$p->add("<ajaxdocument>".$s."</ajaxdocument>");							return $p;		}						function defaultAction($p)		{						$this->goToUsecase("Start");					}	}?>