<?	class UCShowControlForm extends UseCase 	{		function action($p) 		{			global $db;			if(isset($_GET['oid']) &&  is_numeric($_GET['oid'])) {			 	$id = $_GET['oid'];								$cf = new ClassFactory;        	    $s = $cf->showControlForm($this, $id);        	   						}				$p->add("<ajaxdocument>".$s."</ajaxdocument>");							return $p;		}						function defaultAction($p)		{				$this->goToUsecase("Start");					}	}?>