<?	class UCShowProduct extends UseCase 	{		function defaultAction($p)		{				$s = $this->eachPage();			if(isset($_GET['id']) && is_numeric($_GET['id'])) {				$id = $_GET['id'];				$s .= $this->resQuery("SELECT * FROM products WHERE id_products='$id'","productitem","item");			} else {				$s .= "<relocate><url>index.php</url></relocate>";			}			$p->add("<document>".$s."</document>");			return $p;		}	}?>