<?	class UCRememberPassword extends UseCase 	{		function defaultAction($p)		{		        global $db;										$s = $this->eachPage();						if(strlen($_POST['f_mail']) > 0) {					$mail = $_POST['f_mail'];								$res = $db->query("SELECT * FROM r_user WHERE mail='$mail'");				if(count($res) > 0) {					foreach($res as $r) {						//$this->mailDocument($r['mail'], "[WebShop] Напоминание пароля", 'mail_remember_password', $r);					}					$s .= "<relocate><url>?usecase=RememberPassword&amp;id=1/</url></relocate>";				} else {					$s .= $this->getDocument('no_user_found');				}			} else if(isset($_GET['id'])) {					$s .= $this->getDocument('remember_done');			} else {				$s .= $this->getDocument('password_must_be_safe');			}			$p->add("<document>".$s."</document>");			return $p;		}	}?>