<?	class UCStart extends UseCase 	{		function defaultAction($p)		{						$s = $this->eachPage();			$s .= $this->resQuery("SELECT * FROM headline ORDER BY tmark DESC LIMIT 0,1", "headline", "item");						$s .= $this->resQuery("SELECT * FROM products WHERE is_lead = '1' ORDER  BY c_price DESC LIMIT 0,4", "fproductlist", "item");						$p->add("<document>".$s."</document>");			return $p;		}	}?>